import streamlit as st
import pandas as pd
import matplotlib.pyplot as plt
# st.title('Data Analysis demo,',help="Demo project from AI department", )
st.image("data:image/png;base64,", width=100)
st.markdown(
    "<h1 style='color: rgb(130, 109, 186);'>Data Analysis demo</h1>",
    unsafe_allow_html=True
)

uploaded_file = st.file_uploader("Choose a CSV file", type="csv")
if uploaded_file is not None:
    st.write("File uploaded successfully!")
    df=pd.read_csv(uploaded_file)
    st.write("Data Preview:")
    st.dataframe(df.head())
    st.write("Data Summary:")
    st.write(df.describe())
    st.write("Fitering Data:    ")
    columns = df.columns.tolist()
    selected_column= st.selectbox("Select a column to filter", columns)
    unique_values = df[selected_column].unique()
    selected_value = st.selectbox("Select a value to filter by", unique_values)
    filtered_df = df[df[selected_column] == selected_value]   
    st.write(filtered_df)

    st.subheader("Plotting Data:")
    x_column = st.selectbox("Select X-axis column", columns)
    y_column = st.selectbox("Select Y-axis column", columns)
    if st.button("Generate Plot"):
        st.line_chart(filtered_df.set_index(x_column)[y_column], color="#11ce11")
        st.bar_chart(filtered_df.set_index(x_column)[y_column])
        st.area_chart(filtered_df.set_index(x_column)[y_column])
        #st.line_chart(data=filtered_df, *, x=None, y=None, x_label=None, y_label=None, color=None, width="stretch", height="content", use_container_width=None)
    
else:
    st.write("Please upload a CSV file to get started.")

    
